import React, { useState } from 'react';
import Square from './components/Square';
import calculateWinner from './components/CalculateWinner'; // Import the calculateWinner function
import './App.css';

const App = () => {
  const initialSquares = Array(9).fill(null)

  const [squares, setSquares] = useState(initialSquares)
  const [xIsNext, setXIsNext] = useState(true)

  const handleClick = (i) => {
    const newSquares = [...squares]

    if (newSquares[i] === null && !calculateWinner(newSquares)) {
      newSquares[i] = xIsNext ? 'üéÖüèª' : 'üéÑ'
      setSquares(newSquares)
      setXIsNext(!xIsNext)
    }
  }

  const handleRestart = () => {
    setSquares(initialSquares)
    setXIsNext(true)
  }

  return (
    <>
      <h1>‚ùÑÔ∏èTic Tac TOE‚ùÑÔ∏è</h1>
      <Square squares={squares} handleClick={handleClick} />
      <div>
        {calculateWinner(squares) ? `Winner: ${calculateWinner(squares)}` : 'Next player: ' + (xIsNext ? 'X' : 'O')}
      </div>
      <button onClick={handleRestart}>Restart Game</button>
    </>
  )
}

export default App
